import React from "react";

const api = {
  key: '7beedcd716bb91a99f2dfbd7d36d07d9',
  base_url: 'https://api.openweather.org/data/2.5/'
}

function App() {
  const dateBuilder = (d) => {

    let date = String(new window.Date()); //creates a new date object with current date and time
    date = date.slice(3,15); 

    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    let day = days[d.getDay()];

    return `${day} ${date}`
  }



  return (
    <div className="app">
      <main>
        <div className='search-box'>
          <input type="text"
            className='search-bar'
            placeholder='Search...'
          />
        </div>

        <div className='location-box'>
          <div className='location'>Calgary, Canada</div>
            <div className='date'>{dateBuilder(new Date())}</div>
        </div>

        <div className='weather-box'>
          <div className='temp'> 
            15 degrees 
          </div>
          <div className='temp'>Sunny</div>
        </div>


      </main>
    </div>
  );
}

export default App;
